package operators;

public class add {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=2;
		int b=5;
		System.out.println("a+b="+(a+b));

		a=4;
		b=2;
		System.out.println("a-b="+(a-b));

		a=5;
		b=3;
		System.out.println("a*b="+(a*b));

		a=8;
		b=4;
		System.out.println("a/b="+(a/b));
	}

}
